import re
import json

def extract_entities(sentence):
    entities = []
    for entity in ['Machine Domain', 'Physical Device', 'Environment Entity', 'Design Domain', 'Requirements', 'Shared Phenomena']:
        entities.append(re.findall(r'\b' + entity + r'\b', sentence, re.IGNORECASE))
    return entities

def main():
    sentence = input()
    entities = extract_entities(sentence)
    result = {}
    for i in range(len(entities)):
        result[entities[i][0]] = entities[i]
    print(json.dumps(result))

if __name__ == '__main__':
    main()
